package prova3bi.Cinema.Domain.Entities;

import java.io.ByteArrayInputStream;
import java.io.FileInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.util.Base64;

import prova3bi.Cinema.Data.Abstractions.Builder;
import prova3bi.Cinema.Data.Abstractions.Builder.Is;
import prova3bi.Cinema.Data.Abstractions.Column;
import prova3bi.Cinema.Data.Abstractions.IEnumColumn;
import prova3bi.Cinema.Data.Abstractions.Table;
import prova3bi.Cinema.Domain.Validations.Error;
import prova3bi.Cinema.Domain.Validations.ErrorList;
import prova3bi.Cinema.Domain.Validations.Validator;

@Table(nome = "Movies")
public class Movie extends Entity {
	public enum Soundtrack implements IEnumColumn {
		Dublado(1, "Audio dublado"), Legendado(2, "Audio original com legenda"), Orignal(3, "Audio original");

		Soundtrack(int value, String description) {
			this.description = description;
			this.value = value;
		}

		private String description;
		private int value;

		public String description() {
			return description;
		}

		@Override
		public int valor() {
			return value;
		}
	}
	@Column(nome = "audioTrack", tipoSql = "INTEGER")
	public Soundtrack audioTrack;
	@Column(nome = "synopsis", tipoSql = "TEXT")
	public String synopsis;
	@Column(nome = "title", tipoSql = "TEXT")
	public String title;

	// arquivo de imagem encodado em base64
	@Column(nome = "encodedPoster", tipoSql = "BLOB")
	public String encodedPoster;

	@Builder(Is.Insert)
	public Movie(String title, String pathToPoster, String sinopsys, Soundtrack trilhaSonora) {
		super(-1);
		this.title = title;
		this.synopsis = sinopsys;
		this.audioTrack = trilhaSonora;
		if (pathToPoster.isEmpty()) {
			this.encodedPoster = genericMoviePosterBase64;
		} else {
			this.encodedPoster = encodeFileToBase64(pathToPoster);
		}
	}

	@Builder(Is.Read)
	public Movie(int MoviesId, Soundtrack audioTrack, String encodedPoster, String sinopsys, String title) {
		super(MoviesId);
		this.title = title;
		this.synopsis = sinopsys;
		this.audioTrack = audioTrack;
		this.encodedPoster = encodedPoster;
	}

	@Builder(Is.Read)
	public Movie(int MoviesId) {
		super(MoviesId);
	}

	public InputStream getPosterStream() {
		byte[] data = Base64.getDecoder().decode(this.encodedPoster);
		return new ByteArrayInputStream(data);
	}

	private static String encodeFileToBase64(String path) {
		byte[] bytes = null;
		try {
			var stream = new FileInputStream(path);
			bytes = stream.readAllBytes();
			stream.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
		return Base64.getEncoder().encodeToString(bytes);
	}

	// Poster generico para nao ficar vazio
	// TODO escolher um arquivo menor
	private static String genericMoviePosterBase64 = "";

	private static Validator<Movie> validator = new Validator<Movie>()
			.add(movie -> movie.audioTrack != null, movie -> new Error("audioTrack", "!"))
			.add(movie -> movie.title != null, movie -> new Error("title", "!"));

	@Override
	public ErrorList isValid() {
		return validator.TestAll(this);
	}

}
